    display: none !important;
  }
  .navbar .nav-icons {
    display: flex;
    margin-left: auto;
  }
  .navbar .navbar-nav .nav-item {
    display: inline-block;
    /* shrink-wrap to content */
    width: auto;
    /* no forced width */
    margin: 0;
    /* reset Bootstrap margins if any */
    width: 50%;
  }
  .navbar .navbar-nav .nav-link {
    padding-left: 0;
    padding-right: 0;
  }
  .navbar {
